mapper.select:
 select
 ,
 edu_coupon.title,
 edu_coupon.start_time,
 edu_coupon.end_time,
 edu_coupon.limitamount,
 edu_coupon.type,
 edu_coupon.optusername
 from edu_coupon_code
 left join edu_coupon on edu_coupon_code.coupon_id=edu_coupon.id


 		and edu_coupon_code.status=#{e.status}


			and edu_coupon.type=#{e.type}


			and edu_coupon_code.coupon_code = #{e.couponCode}


			and edu_coupon_code.request_id =#{e.requestId}


			and edu_coupon_code.coupon_id=#{e.couponId}


 ORDER BY edu_coupon_code.create_time DESC

select
 count(1)
 from edu_coupon_code
 left join edu_coupon on edu_coupon_code.coupon_id=edu_coupon.id


 		and edu_coupon_code.status=#{e.status}


			and edu_coupon.type=#{e.type}


			and edu_coupon_code.coupon_code = #{e.couponCode}


			and edu_coupon_code.request_id =#{e.requestId}


			and edu_coupon_code.coupon_id=#{e.couponId}

select edu_coupon_code.id, edu_coupon.teacher_id, edu_coupon_code.status, edu_coupon.amount, edu_coupon.limitamount, user_expand.realname, edu_coupon.start_time, edu_coupon.end_time, edu_coupon_code.coupon_code, edu_coupon.type from edu_coupon_code left join edu_coupon on edu_coupon_code.coupon_id = edu_coupon.id left join edu_teacher on edu_coupon.teacher_id = edu_teacher.id left join user_expand on user_expand.user_id = edu_teacher.user_id where edu_coupon_code.user_id = #{e.userId} and edu_coupon_code.status = #{e.status}
and edu_coupon.type = #{e.type}
order by edu_coupon.end_time

select count(1) from edu_coupon_code left join edu_coupon on edu_coupon_code.coupon_id = edu_coupon.id left join edu_teacher on edu_coupon.teacher_id = edu_teacher.id left join user_expand on user_expand.user_id = edu_teacher.user_id where edu_coupon_code.user_id = #{e.userId} and edu_coupon_code.status = #{e.status}
and edu_coupon.type = #{e.type}
order by edu_coupon.end_time
