mapper.select:
select , edu_trxorder_detail.source_price, edu_trxorder_detail.current_price, edu_trxorder_detail.course_name, edu_trxorder_detail.request_id from edu_trxdetail_opt_record left join edu_trxorder_detail on edu_trxdetail_opt_record.trxorder_detail_id = edu_trxorder_detail.id
and edu_trxdetail_opt_record.type in('COURSEAUDIT','COURSESUCCESS','COURSECANCEL')
and edu_trxdetail_opt_record.type = #{e.type}
and edu_trxdetail_opt_record.user_type = #{e.userType}
and edu_trxdetail_opt_record.applicant = #{e.applicant}
and edu_trxorder_detail.request_id = #{e.requestId}
order by edu_trxdetail_opt_record.create_time desc

            select
            count(1)
            from edu_trxdetail_opt_record
            left join edu_trxorder_detail on edu_trxdetail_opt_record.trxorder_detail_id = edu_trxorder_detail.id


            		and edu_trxdetail_opt_record.type in('COURSEAUDIT','COURSESUCCESS','COURSECANCEL')


            		and edu_trxdetail_opt_record.type = #{e.type}


	            	and edu_trxdetail_opt_record.user_type = #{e.userType}


	            	and edu_trxdetail_opt_record.applicant = #{e.applicant}


	            	and edu_trxorder_detail.request_id = #{e.requestId}


            order by edu_trxdetail_opt_record.create_time desc
